import React, { Component } from 'react';
import axios from 'axios';

export default class GetCars extends Component {

    state = {
        cars: [],
        cars_fetch: [],
        start_date: '',
        end_date: '',
        car_class: '',
    }

    async componentDidMount() {
        var res = await axios.get('http://localhost:8090/cars/getAll');
        this.setState({
            cars: res.data
        })
    }

    onChangeStartDate = (event) => {
        this.setState({
            start_date: event.target.value
        })
    }

    onChangeEndDate = (event) => {
        this.setState({
            end_date: event.target.value
        })
    }

    onChangeClass = (event) => {
        this.setState({
            car_class: event.target.value
        })
    }

    onSubmit = async e => {
        e.preventDefault();
        if (this.state.start_date === "" || this.state.end_date === "") {
            window.alert('You must enter an start and end date');
        } else {
            if (this.state.start_date >= this.state.end_date) {
                window.alert('The end date must be later than the start date')
            } else {
                var res = await axios.get("http://localhost:8090/cars/getAllAvailable",
                    { params: { start: this.state.start_date, end: this.state.end_date, class: this.state.car_class } })
                this.setState({
                    cars: res.data
                })
            }
        }
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-4 mt-5">
                        <div className="card card-body text-white bg-secondary">
                            <h3>
                                Get All Cars
                            </h3>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label for="startDate">Start Date</label>
                                    <input type="date" className="form-control" id="startDate" aria-describedby="dateHelp" onChange={this.onChangeStartDate} />
                                </div>
                                <div className="form-group">
                                    <label for="endDate">End Date</label>
                                    <input type="date" className="form-control" id="endDate" aria-describedby="dateHelp" onChange={this.onChangeEndDate} />
                                </div>
                                <small id="datelHelp" className="form-text text-muted text-light">Type the start and end date to get the cars</small>
                                <div className="form-group mt-1">
                                    <select className="custom-select form-control" id="className" onChange={this.onChangeClass}>
                                        <option value="null" selected>Choose a car class</option>
                                        <option value="Sedan">Sedan</option>
                                        <option value="Compact">Compact</option>
                                        <option value="Pick Up">Pick Up</option>
                                        <option value="Sport">Sport</option>
                                    </select>
                                </div>
                                <button type="submit" className="btn btn-primary mt-3">Consult</button>
                            </form>
                        </div>
                    </div>
                    <div className="col-md-8 mt-5">
                        <table className="table table-bordered table-striped table-secondary">
                            <thead>
                                <tr>
                                    <th scope="col">Car ID</th>
                                    <th scope="col">Class</th>
                                    <th scope="col">Brand</th>
                                    <th scope="col">Model</th>
                                    <th scope="col">Year</th>
                                    <th scope="col">Transmission</th>
                                    <th scope="col">Color</th>
                                    <th scope="col">Price/day</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.cars.map(car => (
                                        <tr key={car.id}>
                                            <th scope="row">{car.carID}</th>
                                            <td>{car.carClass}</td>
                                            <td>{car.brand}</td>
                                            <td>{car.model}</td>
                                            <td>{car.year}</td>
                                            <td>{car.transmission}</td>
                                            <td>{car.color}</td>
                                            <td>{car.pricePerDay}</td>
                                        </tr>)
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    };
}
