import React, { Component } from 'react';
import axios from 'axios';

export default class Register extends Component {
    onSubmit = async e => {
        e.preventDefault();
        var data = new FormData();
        data.append("email", document.getElementById("email").value);
        data.append("password", document.getElementById("password").value);
    
        if (data.get('email') === "" || data.get('password') === "") {
            window.alert('You must enter an email and a password');
        } else {
            if (data.get('startDate') >= data.get('endDate')) {
                window.alert('The end date must be later than the start date')
            } else {
                var res = await axios.get("http://localhost:8090/cars/getAllAvailable",
                    { params: { start: data.get('startDate'), end: data.get('endDate'), class: data.get('className') } })
                this.setState({
                    cars: res.data,
                    start_date: data.get('startDate'),
                    end_date: data.get('endDate'),
                    car_class: data.get('className'),
                    queryMade: '1'
                })
            }
        }
    }
    
    render() {
        return (
            <div className="container mt-5">
                <div className="card card-body text-white bg-secondary mx-auto" style={{width: '40%'}}>
                    <h3>
                        User registration
                    </h3>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group"  onSubmit={this.onSubmit}> 
                            <label for="email">Email</label>
                            <input type="text" className="form-control" id="email" placeholder="Enter your email address" />
                        </div>
                        <div className="form-group mb-4 mt-2">
                            <label for="password">Password</label>
                            <input type="password" className="form-control" id="password" placeholder="Enter your password" />
                        </div>
                        <div className="container container-fluid">
                            <button type="submit" className="btn btn-primary mt-3">Register</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    };
}
